namespace TuProyecto.Domain.Models.<Modulo>;

using System;
using TuProyecto.Domain.Core.Models;
using TuProyecto.Domain.Core.Models.Exceptions;

public class <Entidad> : Entity
{
    public string Name { get; private set; }

    // Constructor privado - solo accesible desde el método factory
    private <Entidad>(Guid id) : base(id)
    {
        Name = null;
    }

    // Método factory estático para crear instancias válidas
    public static <Entidad> Create(Guid id)
    {
        if (id.Equals(Guid.Empty))
        {
            throw new InvalidValueException("The <Entidad> id can't be empty.");
        }

        <Entidad> entity = new <Entidad>(id);
        entity.UpdateEditDate();
        return entity;
    }

    // Método de comportamiento con validaciones
    public void SetName(string name)
    {
        if (string.IsNullOrWhiteSpace(name))
        {
            throw new InvalidValueException("The <Entidad> name can't be null/empty.");
        }

        Name = name;
        UpdateEditDate();
    }
}
