namespace TuProyecto.Data.Mappings.<Modulo>;

using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;
using TuProyecto.Domain.Models.<Modulo>;

public class <Entidad>Mapping : IEntityTypeConfiguration<<Entidad>>
{
    public void Configure(EntityTypeBuilder<<Entidad>> builder)
    {
        // Configuración de tabla
        builder.ToTable("<Tabla>");

        // Clave primaria
        builder.HasKey(e => e.Id);

        // Propiedades
        builder.Property(e => e.Name)
            .HasMaxLength(100)
            .IsRequired();

        builder.Property(e => e.CreationDate)
            .IsRequired();

        builder.Property(e => e.EditDate)
            .IsRequired();

        // Relaciones
        // builder.HasMany(e => e.RelatedEntities)
        //     .WithOne(r => r.<Entidad>)
        //     .HasForeignKey(r => r.<Entidad>Id);
    }
}
