namespace TuProyecto.Infraestructure.InversionOfControl.Inyectors;

using Microsoft.Extensions.DependencyInjection;
using TuProyecto.Application.Interfaces.<Modulo>;
using TuProyecto.Application.Services.<Modulo>;
using TuProyecto.Data.Repositories.<Modulo>;
using TuProyecto.Domain.Repositories.<Modulo>;

public static class <Nombre>Inyector
{
    public static void Inyect(IServiceCollection services)
    {
        // Repositorios
        services.AddScoped<I<Entidad>Repository, <Entidad>Repository>();

        // Servicios de aplicación
        services.AddScoped<I<Entidad>AppService, <Entidad>AppService>();

        // Otros servicios relacionados con esta capa/módulo
        // services.AddScoped<IOtroServicio, OtroServicio>();
    }
}
